import React, { useState } from 'react';
import Drawer from '@mui/material/Drawer';
import Button from '@mui/material/Button';
import Tabs from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Accordion from '@mui/material/Accordion';
import AccordionSummary from '@mui/material/AccordionSummary';
import AccordionDetails from '@mui/material/AccordionDetails';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';

const MyComponent = () => {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [selectedTab, setSelectedTab] = useState(0);

  const toggleDrawer = (open) => () => {
    setDrawerOpen(open);
  };

  const handleChange = (event, newValue) => {
    setSelectedTab(newValue);
  };

  const drawer = (
    <div>
      <Tabs
        orientation="vertical"
        variant="scrollable"
        value={selectedTab}
        onChange={handleChange}
      >
        <Tab label="Account" />
        <Tab label="Delivery" />
        <Tab label="Payment" />
        <Tab label="Product" />
      </Tabs>
    </div>
  );

  const accordion = (
    <Box>
      <Accordion>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1a-content"
          id="panel1a-header"
        >
          <Typography>Got a question?</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography>
            Lorem ipsum is a name for a common type of placeholder text. 
            Also known as filler or dummy text, this is text copy that serves to fill a space without saying...
          </Typography>
        </AccordionDetails>
      </Accordion>
      <Accordion>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel2a-content"
          id="panel2a-header"
        >
          <Typography>Got another question?</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Typography>
            Lorem ipsum is a name for a common type of placeholder text. 
            Also known as filler or dummy text, this is text copy that serves to fill a space without saying...
          </Typography>
        </AccordionDetails>
      </Accordion>
    </Box>
  );

  return (
    <div>
      <Button onClick={toggleDrawer(true)}>Open Sidebar</Button>
      <Drawer anchor={'left'} open={drawerOpen} onClose={toggleDrawer(false)}>
        {drawer}
      </Drawer>
      {selectedTab === 0 && accordion}
    </div>
  );
}

export default MyComponent;
