+        placeholder="firstname"
+        {...register("firstname")}
+        style={{ width: "80%" }}
+        InputProps={{
+          startAdornment: (
+            <InputAdornment position="end">
+              <PersonIcon />
+            </InputAdornment>
+          ),
+        }}
+      />
+      <TextField
+        label="Last Name"
+        variant="outlined"
+        className="input-blogin margin-tb-1"
+        placeholder="lastname"
+        {...register("lastname")}
+        style={{ width: "80%" }}
+        InputProps={{
+          startAdornment: (
+            <InputAdornment position="end">
+              <PersonIcon />
+            </InputAdornment>
+          ),
+        }}
+   
+      />
+      <TextField
+        label="Email"
+        variant="outlined"
+        placeholder="Email"
+        className="input-blogin margin-tb-1"
+        {...register("email")}
+        style={{ width: "80%" }}
+        InputProps={{
+          startAdornment: (
+            <InputAdornment position="end">
+              <EmailIcon />
+            </InputAdornment>
+          ),
+        }}
+      />
+      <TextField
+        label="Password"
+        variant="outlined"
+        className="input-blogin margin-tb-1"
+        placeholder="Enter Password"
+        {...register("password", { required: true })}
+        style={{ width: "80%" }}
+        InputProps={{
+          startAdornment: (
+            <InputAdornment position="end">
+              <HttpsIcon />
+            </InputAdornment>
