+        style={{ width: "80%" }}
+        InputProps={{
+          startAdornment: (
+            <InputAdornment position="end">
+              <PersonIcon />
+            </InputAdornment>
+          ),
+        }}
+      />
+      <TextField
+        label="Last Name"
+        variant="outlined"
+        className="input-blogin margin-tb-1"
+        placeholder="lastname"
+        {...register("lastname")}
+        style={{ width: "80%" }}
+        InputProps={{
+          startAdornment: (
+            <InputAdornment position="end">
+              <PersonIcon />
+            </InputAdornment>
+          ),
+        }}
+   
+      />
+      <TextField
+        label="Email"
+        variant="outlined"
+        placeholder="Email"
+        className="input-blogin margin-tb-1"
+        {...register("email")}
+        style={{ width: "80%" }}
+        InputProps={{
+          startAdornment: (
+            <InputAdornment position="end">
+              <EmailIcon />
+            </InputAdornment>
+          ),
+        }}
+      />
+      <TextField
+        label="Password"
+        variant="outlined"
+        className="input-blogin margin-tb-1"
+        placeholder="Enter Password"
+        {...register("password", { required: true })}
+        style={{ width: "80%" }}
+        InputProps={{
+          startAdornment: (
+            <InputAdornment position="end">
+              <HttpsIcon />
+            </InputAdornment>
+          ),
+          endAdornment:(
+            <IconButton onClick={handleShowPassword} edge="end">
+            {showPassword ? <Visibility /> : <VisibilityOff />}
+          </IconButton>
+          )
+        }}
+        type={showPassword ? 'text' : 'password'}
+      />
+      <TextField
+        label="Confirm Password"
+        variant="outlined"
+        className="input-blogin margin-tb-1"
+        placeholder="Confirm Password"
+        value={confirmPassword}
+        onChange={handleConfirmPassword}
+        style={{ width: "80%" }}
+        InputProps={{
+          startAdornment: (
+            <InputAdornment position="end">
+              <KeyIcon />
+            </InputAdornment>
+          ),
+          endAdornment:(
+            <IconButton onClick={handleShowCPassword} edge="end">
+            {showConfirmPassword ? <Visibility /> : <VisibilityOff />}
+          </IconButton>
+          )
+        }}
+        type={showConfirmPassword ? 'text' : 'password'}
