import { Button, TextField } from "@mui/material";
import Checkbox from '@mui/material/Checkbox';
import FormControlLabel from '@mui/material/FormControlLabel';

const Register = ({ handleSubmit, register, RegisterR }) => {
  return (
    <form onSubmit={handleSubmit(RegisterR)} className="column margin-se-1" style={{width: '550px'}}>
      <TextField
        label="First Name"
        variant="outlined"
        className="input-b"
        {...register("firstname")}
        InputProps={{
          startAdornment: (
            <InputAdornment position="end">
              <EmailIcon />
            </InputAdornment>
          ),
        }}
      />
      <TextField
        label="Last Name"
        variant="outlined"
        className="input-b"
        {...register("lastname")}
      />
      <TextField
        label="Email"
        variant="outlined"
        className="input-b"
        {...register("email")}
      />
      <TextField
        label="Enter Password"
        variant="outlined"
        className="input-b"
        {...register("Enter Password")}
      />
      <TextField
        label="Confirm Password"
        variant="outlined"
        className="input-b"
        {...register("Confirm Password")}
      />
         <FormControlLabel
      control={<Checkbox  />}
      label="Using the website, you accept the terms and conditions"
    />
      <Button type="submit">Register</Button>
    </form>
  );
};

export default Register;
