commits.content:
html,
body {
    min-height: 100%;
    padding: 0;
    margin: 0;
    font-family: Vazirmatn;
}

html,
body,
#__next {
    display: flex;
    flex-direction: column;
    flex-grow: 1;
}

a {
    color: inherit;
    text-decoration: none;
}

ol {
    list-style-type: persian;
}

$dark-gray: #222;
$light-gray: #ddd;

$colors: (
    primary: #fbaf17,
    secondary: #4caf50,
    buy: #41b62a,
    sell: #ff3f3f,
    dark-gray: $dark-gray,
    light-gray: $light-gray,
    white: white,
    gray: gray,
    black: black,
    bg: black,
    fg: white,
    bg2: $dark-gray,
    fg2: $light-gray,
    green: #2e7d32,
    yellow: #ed6c02,
    red: #d32f2f,
    bg-gold: #dba63c,
);

@each $k, $v in $colors {
    .back-#{$k} {
        background: $v;
    }
    .color-#{$k} {
        color: $v;
    }
    .border-#{$k} {
        border: 1px solid $v;
    }
}

html {
    color-scheme: dark;
}

body {
    color: black;
    background: black;
}

