@media screen and (max-width: 800px) {
  .container .body1 .body1-NameLogo {
    display: none;
  }
  .container .body1 .body1-AdvancedSearchBar {
    margin-top: 200px;
    width: 80%;
    flex-direction: column;
    height: 200px;
    background-color: rgb(0, 22, 45);
    box-shadow: none;
  }
  .container .body1 .body1-AdvancedSearchBar .AdvancedInputSearchDiv {
    width: 100%;
    margin-bottom: 20px;
    background-color: rgb(235, 235, 235);
    border-radius: 20px;
    box-shadow: rgba(255, 255, 255, 0.712) 0px 0px 14px;
    position: relative;
    border-radius: 5px;
    height: 40px;
  }
  .container .body1 .body1-AdvancedSearchBar .BasicButtonDiv {
    width: 100%;
    margin-bottom: 20px;
    background-color: rgb(235, 235, 235);
    border-radius: 20px;
    box-shadow: rgba(255, 255, 255, 0.712) 0px 0px 14px;
    position: relative;
    right: 0;
    left: 0;
    border-radius: 5px;
    border-left: none;
    height: 40px;
  }
  .container .body1 .body1-AdvancedSearchBar .AdvancedButtonDiv {
    width: 100%;
    margin-bottom: 20px;
    background-color: rgb(235, 235, 235);
    border-radius: 20px;
    box-shadow: rgba(255, 255, 255, 0.712) 0px 0px 14px;
    position: relative;
    border-radius: 5px;
    border-left: none;
    height: 40px;
  }
  .container .body1 .body1-AdvancedSearchBar .AdvancedButtonDiv button {
    height: 80%;
    width: 95%;
  }
  .container .body1 .body1-AdvancedSearchBar .BasicButtonDiv button {
    height: 80%;
    width: 95%;
  }

  .tab button {
    font-size: 10px;
  }
}
@media screen and (max-width: 950px) {
  .searchInputs {
    width: 100%;
  }
}
@media screen and (max-width: 600px) {
  .tab {
    width: 100%;
  }
  .tab button {
    font-size: 10px;
  }
}
@media screen and (max-width: 400px) {
  .tab {
    width: 100%;
  }
  .tab button {
    font-size: 7px;
  }
}
@media screen and (min-width: 800px) {
  .myanimationA {
    position: absolute;
    right: 0;
    transition: all 3s;
    transition-delay: 3s;
  }
  .myanimationI {
    position: absolute;
    left: 0;
  }
  .myanimationB {
    transition-property: opacity;
    transition-duration: 2s;
    opacity: 0;
    position: absolute;
    right: 220px;
  }
  .AdvancedInputSearchDivInAnimation {
    width: 75% !important ;
  }
}
